import React, { Component } from 'react'

export default class TopicText extends Component {
  constructor(props) {
    super(props);
    this.state = {
      topicText: "hello"
    }
  }

  updateTopic(text) {
    this.setState({
      topicText: text
    });
  }

  render() {
    return (
      <header className="topic">{this.state.topicText}</header>
    )
  }
}

// export default TopicText
