import React, { Component, useState }  from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { PreviousButton, RandomButton, NextButton } from './components/nav-buttons';
import { InfoButtonWrapper, DButton, IButton, CButton, EButton, ViewAll } from './components/info-buttons'


class Flashcard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      topic: null,
    }
  }

  render() {
    return (
          <div className="main-UI">
            <Card />
            <NavButtonWrapper />
          </div>
        )
      }
}

const Card = () => {
  return (
    <section className="card">
      <TopicText />
      <MidsectionWrapper />
      <AnswerText />
    </section>
  )
}

let topicText = "TOPIC"
class TopicText extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      topicText: topicText
    }
  }

  render() {
    return (
      <header className="topic">{this.state.topicText}</header>
    )
  }
}

const MidsectionWrapper = () => {
  return (
    <section className="mid">
      <QuestionText />
      <InfoButtonWrapper />
    </section>
  )
}

let text = "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Voluptates consectetur sint nesciunt. Doloremque quisquam ipsum sint enim sequi quas aut ipsa aliquam sunt, nemo molestiae similique sapiente quam culpa excepturi."
class QuestionText extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      questionText: text
    }
  }

  render() {
    return(
      <section className="question">
            <p>{this.state.questionText}</p>
      </section>
      )
    }
  }

