let answerText = "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Sit voluptates ipsa iure a modi dolore rem impedit porro nihil facilis!"
class AnswerText extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      answerText: answerText
    }
  }
  render() {
    return (
        <section className="answer">{this.state.answerText}</section>
    )
  }
}

const NavButtonWrapper = () => {
  return (
  <section className="nav-btn-wrapper">
    <PreviousButton />
    <RandomButton />
    <NextButton />
  </section>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <Flashcard />
  </React.StrictMode>,
document.getElementById('root')
);
