import React, { Component } from 'react'

export default class AnswerText extends Component {
  constructor(props) {
    super(props);
    this.state = {
      answerText: 'Placeholder answer'
    }
  }
  render() {
    return (
        <section className="answer">{this.state.answerText}</section>
    )
  }
}

// export default AnswerText
